const Post = ({ date, image, title }) => {
  return (
    <div className="post">
      {
        image 
        ? <img alt={image.description} src={`https:${image.file.url}`} />
        : null
      }

      <div className="description">{description}</div>

      <div className="text">
        <h2>{title}</h2>
        <h3>{date.substring(0, 10)}</h3>
      </div>

      <style jsx>{`
        .post {
          position: relative;
          margin: 10px;
          width: 300px;
          color: white;
          cursor: pointer;
        }
        .description {
          position: absolute;
          top: 0;
          padding: 10px;
          box-sizing: border-box;
          background: linear-gradient(0deg, rgba(0, 0, 0, 0) 20%, rgba(0, 0, 0, 1) 100%);
          height: 100px;
          opacity: 0;
          transition: opacity 0.5s;
        }
        .post:hover .description {
          opacity: 1;
        }
        .text {
          position: absolute;
          bottom: 3px;
          padding: 10px;
          box-sizing: border-box;
          width: 100%;
          height: 70px;
          background: linear-gradient(0deg, rgba(0, 0, 0, 0.5) 20%, rgba(0, 0, 0, 0) 100%);
        }
        h2,
        h3 {
          margin: 5px;
        }
        h2 {
          margin-bottom: 0;
        }
        h3 {
          margin-top: 0;
          font-size: 0.8em;
          font-weight: 400;
        }
        img {
          max-width: 300px;
        }
      `}</style>
    </div>
  )
}

export default Post
